import React from "react";

import BgImg from "../../static/bg.jpg"

export default class Video extends React.Component {
    constructor(props) {
        super(props);
        this.state = {}
    }

    render() {
        const url = require('../../media/gaoxiao.mp4')
        const style = {
            width: '100%',
            height: '170px',
            fontSize: '25px',
            margin: '0 auto',
            // textAlign: 'center',
            color: 'white',
            fontWeight: '800',
            backgroundImage: `url(${BgImg})`,
            backgroundSize: '100% 100%'
        }
        return (
            <div style={{width: '100%', height: 'auto'}}>
                <div style={style}><span style={{display: 'block',width: '150px',textAlign: 'center',margin: '10px auto', overflow: 'auto'}}>搞 笑 视 频</span></div>
                <video style={{display: 'block', margin: '0 auto'}} src={url.default} controls='controls'/>
            </div>
        )
    }
}
