import React from "react";

class LoginOut extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            isHover: false
        }
    }

    handleEnter(){
        this.setState({
            isHover: !this.state.isHover
        })
    }

    handleOut(){
        this.setState({
            isHover: false
        })
    }

    handleDel(){
        let {username,password} = this.props
        // sessionStorage.clear();  //清除登陆信息，刷新会退出页面
        // sessionStorage.removeItem("username");  //刷新会退出页面
        localStorage.removeItem('username');  //刷新不会退出页面
        // localStorage.clear();
        return alert('清除成功');
    }

    render() {
        let style = {}
        let {isHover} = this.state
        if (isHover){
            style = {
                color: '#0000FF',
                textDecoration: 'underline',
                cursor: 'pointer'
            }
        }else {
            style = {
                color: 'black'
            }
        }
        return (
            <div>
                <a className='_a' style={ style } onMouseEnter={this.handleEnter.bind(this)} onMouseLeave={this.handleOut.bind(this)} onClick={this.handleDel.bind(this)}>登出</a>
            </div>
        )
    }
}
export default LoginOut;
