import React from "react";

class GetData extends React.Component{
    constructor(props) {
        super(props);
        this.state={}
    }

    componentDidMount() {
        // setInterval(this.handleTime,1000)
    }

    handleTime(){
        let date = new Date();
        let str1 = '/';
        let str2 = ':';
        let mouth = date.getMonth();
        let strData = date.getDate();
        let strSeconds = date.getSeconds();
        if (mouth>0 && mouth<=9){
            mouth='0'+mouth
        }
        if (strData>0 && strData<=9){
            strData='0'+strData
        }
        if (strSeconds>0 && strSeconds<=9){
            strSeconds='0'+strSeconds
        }
        let newData= date.getFullYear()+str1+mouth+str1+strData+' '+'br'+date.getHours()+str2+date.getMinutes()+str2+strSeconds;
        console.log(newData)
        return newData;
    }

    render() {
        return (
            <div>
                <p>{this.handleTime()}</p>
                <button onClick={this.handleTime.bind(this)}>点击</button>
            </div>
        )
    }
}

export default GetData;
