import React from "react";

class UPLoad extends React.Component{
    state={
        fileList:[],
        file:''
    }

    onChange(e){
        this.setState({
            file:e.target.value
        })
    }

    handleUpLoad(){
        const {fileList,file} = this.state
        const fileData = new FormData();
        fileList.push(file);
        fileList.forEach(e => {
            fileData.append('fileList[]',e)
        })
        console.log(file);
        console.log(fileList);
        console.log(fileData.getAll('fileList[]'))
    }

    render() {
        return (
            <div>
                <label>
                    上传的文件：
                    <input type="file" value={this.state.file} onChange={this.onChange.bind(this)}/>
                </label>
                <button onClick={this.handleUpLoad.bind(this)}>上传</button>
                <p>上传的文件有：{this.state.fileList.flatMap(e => {
                    console.log(e)
                    return (
                        <div>
                            {e}
                        </div>
                    )
                })}</p>
            </div>
        )
    }
}

export default UPLoad;
